package com.example.wallpaperapp;

import androidx.appcompat.app.AppCompatActivity;
import androidx.recyclerview.widget.GridLayoutManager;
import androidx.recyclerview.widget.LinearLayoutManager;
import androidx.recyclerview.widget.RecyclerView;

import android.os.Bundle;
import android.view.View;
import android.widget.EditText;
import android.widget.ImageView;
import android.widget.ProgressBar;
import android.widget.Toast;

import com.android.volley.AuthFailureError;
import com.android.volley.Request;
import com.android.volley.RequestQueue;
import com.android.volley.Response;
import com.android.volley.VolleyError;
import com.android.volley.toolbox.JsonObjectRequest;
import com.android.volley.toolbox.Volley;

import org.json.JSONArray;
import org.json.JSONException;
import org.json.JSONObject;

import java.util.ArrayList;
import java.util.HashMap;
import java.util.Map;

public class MainActivity extends AppCompatActivity implements CategoryRVAdapter.CategoryClickInterface{

    //563492ad6f91700001000001def0ec3accba403399bb669b678cd25e
    private EditText searchEdt;
    private ImageView seachIV;
    private RecyclerView categoryRV,wallpaperRV;
    private ProgressBar loadingPB;
    private ArrayList<String> wallpaperArrayList;
    private ArrayList<CategoryRVModal> categoryRVModalArrayList;
    private CategoryRVAdapter categoryRVAdapter;
    private WallpaperRVAdapter wallpaperRVAdapter;
    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.activity_main);

        searchEdt = findViewById(R.id.idEdtSearch);
        seachIV = findViewById(R.id.idIVSearch);
        categoryRV = findViewById(R.id.idRVCategory);
        wallpaperRV = findViewById(R.id.idRVWallpapers);
        loadingPB = findViewById(R.id.idPBLoading);
        wallpaperArrayList = new ArrayList<>();
        categoryRVModalArrayList = new ArrayList<>();
        LinearLayoutManager linearLayoutManager = new LinearLayoutManager(MainActivity.this,RecyclerView.HORIZONTAL,false);
        categoryRV.setLayoutManager(linearLayoutManager);
        categoryRVAdapter = new CategoryRVAdapter(categoryRVModalArrayList,this,this::onCategoryClick);
        categoryRV.setAdapter(categoryRVAdapter);

        GridLayoutManager gridLayoutManager = new GridLayoutManager(this,2);
        wallpaperRV.setLayoutManager(gridLayoutManager);
        wallpaperRVAdapter = new WallpaperRVAdapter(wallpaperArrayList,this);
        wallpaperRV.setAdapter(wallpaperRVAdapter);

        getCategories();
        getWallpapers();

        seachIV.setOnClickListener(new View.OnClickListener() {
            @Override
            public void onClick(View view) {
                String searchStr = searchEdt.getText().toString();
                if(searchStr.isEmpty()){
                    Toast.makeText(MainActivity.this, "Please enter your search query", Toast.LENGTH_SHORT).show();
                }else{
                    getWallpapersByCategory(searchStr);
                }
            }
        });

    }

    private void getWallpapersByCategory(String category){
        wallpaperArrayList.clear();
        loadingPB.setVisibility(View.VISIBLE);
        String url = "https://api.pexels.com/v1/search?query="+category+"&per_page=30&page=1";
        RequestQueue requestQueue = Volley.newRequestQueue(MainActivity.this);
        JsonObjectRequest jsonObjectRequest = new JsonObjectRequest(Request.Method.GET, url, null, new Response.Listener<JSONObject>() {
            @Override
            public void onResponse(JSONObject response) {
                JSONArray photoArray = null;
                loadingPB.setVisibility(View.GONE);
                try {

                     photoArray = response.getJSONArray("photos");
                    for (int i=0;i<photoArray.length();i++){
                        JSONObject photoObj = photoArray.getJSONObject(i);
                        String imageUrl = photoObj.getJSONObject("src").getString("portrait");
                        wallpaperArrayList.add(imageUrl);
                    }
                    wallpaperRVAdapter.notifyDataSetChanged();
                } catch (JSONException e) {
                    e.printStackTrace();
                }

            }
        }, new Response.ErrorListener() {
            @Override
            public void onErrorResponse(VolleyError error) {
                Toast.makeText(MainActivity.this, "Failed to load wallpapers....", Toast.LENGTH_SHORT).show();
            }
        }){
            @Override
            public Map<String, String> getHeaders() throws AuthFailureError {
                HashMap<String,String> headers = new HashMap<>();
                headers.put("Authorization","563492ad6f91700001000001def0ec3accba403399bb669b678cd25e");
                return headers;
            }
        };
        requestQueue.add(jsonObjectRequest);

    }

    private void getWallpapers() {
        wallpaperArrayList.clear();
        loadingPB.setVisibility(View.VISIBLE);
        String url = "https://api.pexels.com/v1/curated?per_page=30&page=1";
        RequestQueue requestQueue = Volley.newRequestQueue(MainActivity.this);
        JsonObjectRequest jsonObjectRequest = new JsonObjectRequest(Request.Method.GET, url, null, new Response.Listener<JSONObject>() {
            @Override
            public void onResponse(JSONObject response) {
            loadingPB.setVisibility(View.GONE);
            try {
                JSONArray photArray = response.getJSONArray("photos");
                for(int i=0;i<photArray.length();i++){
                    JSONObject photObj = photArray.getJSONObject(i);
                    String imageUrl = photObj.getJSONObject("src").getString("portrait");
                    wallpaperArrayList.add(imageUrl);
                }
                wallpaperRVAdapter.notifyDataSetChanged();
            }catch (JSONException e){
                e.printStackTrace();
            }
            }
        }, new Response.ErrorListener() {
            @Override
            public void onErrorResponse(VolleyError error) {
                Toast.makeText(MainActivity.this, "Failed to load wallpapers.....", Toast.LENGTH_SHORT).show();
            }
        }){
            @Override
            public Map<String, String> getHeaders() throws AuthFailureError {
                HashMap<String,String> headers = new HashMap<>();
                headers.put("Authorization","563492ad6f91700001000001def0ec3accba403399bb669b678cd25e");
                return headers;
            }
        };
        requestQueue.add(jsonObjectRequest);
    }

    private void getCategories() {
        categoryRVModalArrayList.add(new CategoryRVModal("Technology","https://www.investopedia.com/thmb/ooWnJKzULBikIcMgNqZdiRvHHBY=/2121x1414/filters:fill(auto,1)/GettyImages-964033964-ca3290057ccc4024b57e755423572264.jpg"));
        categoryRVModalArrayList.add(new CategoryRVModal("Programming","data:image/jpeg;base64,"));
        categoryRVModalArrayList.add(new CategoryRVModal("Nature","data:image/jpeg;base64,"));
        categoryRVModalArrayList.add(new CategoryRVModal("Travel","data:image/jpeg;base64,"));
        categoryRVModalArrayList.add(new CategoryRVModal("Architect","data:image/jpeg;base64,"));
        categoryRVModalArrayList.add(new CategoryRVModal("Arts","data:image/jpeg;base64,"));
        categoryRVModalArrayList.add(new CategoryRVModal("Music","data:image/jpeg;base64,"));
        categoryRVModalArrayList.add(new CategoryRVModal("Abstract","data:image/jpeg;base64,"));
        categoryRVModalArrayList.add(new CategoryRVModal("Cars","data:image/jpeg;base64,"));
        categoryRVModalArrayList.add(new CategoryRVModal("Flowers","data:image/jpeg;base64,"));
        categoryRVAdapter.notifyDataSetChanged();
    }

    @Override
    public void onCategoryClick(int position) {
        String category = categoryRVModalArrayList.get(position).getCategory();
        getWallpapersByCategory(category);
    }
}